mapper.select:
select name
  from menu
where use_ox = 'O';

select count(*)
from menu
where use_ox='O'
and name=#{name}
and parent_Name=#{parentName}

select MAX(sort)
  from menu
 where parent_name=#{parentName}

mapper.delete:
delete from menu
 where name = #{name}

    and parent_name = #{parentName}

mapper.update:
update menu
   set use_ox='X'
 where menu_id=#{menuId}
